/*
 * deOrphan 1.1.1 - jQuery Plugin
 * https://github.com/megasmack/jquery.deorphan.js
 */
(function($){$.fn.deOrphan=function(){function _deOrphan(node){$(node).contents().each(function(i,node){if(node.nodeType==3&&(typeof(node.textContent)!=='undefined')){node.textContent=node.nodeValue.replace(/ (\S*)$/,'\u00A0$1');}else{_deOrphan(node);}});}return this.each(function(i,el){_deOrphan(el);});};})(jQuery);
